CollectionPageQuery:
query CollectionPageQuery($id: String!) {
  site {
    siteMetadata {
      titleTemplate
      title
      description
    }
  }
  collection: storeCollection(id: {eq: $id}) {
    seo {
      title
      description
    }
    breadcrumbList {
      itemListElement {
        item
        name
        position
      }
    }
    meta {
      selectedFacets {
        key
        value
      }
    }
  }
  allStoreProduct(limit: 5) {
    nodes {
      id: productID
      slug
      sku
      brand {
        brandName: name
        name
      }
      name
      gtin
      isVariantOf {
        productGroupID
        name
      }
      image {
        url
        alternateName
      }
      offers {
        lowPrice
        offers {
          availability
          price
          listPrice
          quantity
          seller {
            identifier
          }
        }
      }
    }
  }
}

ProductPageQuery:
query ProductPageQuery($id: String!) {
  site {
    siteMetadata {
      title
      description
      titleTemplate
      siteUrl
    }
  }
  product: storeProduct(id: {eq: $id}) {
    id: productID
    slug
    seo {
      title
      description
    }
    brand {
      name
    }
    sku
    gtin
    name
    description
    breadcrumbList {
      itemListElement {
        item
        name
        position
      }
    }
    image {
      url
      alternateName
    }
    offers {
      lowPrice
      highPrice
      priceCurrency
      offers {
        availability
        price
        priceValidUntil
        priceCurrency
        itemCondition
        seller {
          identifier
        }
        listPrice
      }
    }
    isVariantOf {
      productGroupID
      name
    }
  }
  allStoreProduct(limit: 5) {
    nodes {
      id: productID
      slug
      sku
      brand {
        brandName: name
        name
      }
      name
      gtin
      isVariantOf {
        productGroupID
        name
      }
      image {
        url
        alternateName
      }
      offers {
        lowPrice
        offers {
          availability
          price
          listPrice
          quantity
          seller {
            identifier
          }
        }
      }
    }
  }
}

ValidateCartMutation:
mutation ValidateCartMutation($cart: IStoreCart!) {
  validateCart(cart: $cart) {
    order {
      orderNumber
      acceptedOffer {
        seller {
          identifier
        }
        quantity
        price
        listPrice
        itemOffered {
          sku
          name
          image {
            url
            alternateName
          }
          brand {
            name
          }
          isVariantOf {
            productGroupID
            name
          }
          gtin
        }
      }
    }
    messages {
      text
      status
    }
  }
}

