NavlinksQuery:
query NavlinksQuery {
  allStoreCollection(limit: 2, filter: {type: {eq: Department}}) {
    nodes {
      slug
      seo {
        title
      }
    }
  }
}

BrowserProductPageQuery:
query BrowserProductPageQuery($locator: [IStoreSelectedFacet!]!) {
  product(locator: $locator) {
    id: productID
    slug
    seo {
      title
      description
    }
    brand {
      name
    }
    sku
    gtin
    name
    description
    breadcrumbList {
      itemListElement {
        item
        name
        position
      }
    }
    image {
      url
      alternateName
    }
    offers {
      lowPrice
      highPrice
      priceCurrency
      offers {
        price
        priceValidUntil
        priceCurrency
        availability
        itemCondition
        seller {
          identifier
        }
        listPrice
      }
    }
    isVariantOf {
      productGroupID
      name
    }
  }
}

