ValidateCartMutation:
mutation ValidateCartMutation($cart: IStoreCart!) {
  validateCart(cart: $cart) {
    order {
      orderNumber
      acceptedOffer {
        seller {
          identifier
        }
        quantity
        price
        listPrice
        itemOffered {
          sku
          name
          image {
            url
            alternateName
          }
        }
      }
    }
    messages {
      text
      status
    }
  }
}

SearchQuery:
query SearchQuery($first: Int!, $after: String, $sort: StoreSort, $term: String, $selectedFacets: [IStoreSelectedFacet!]!) {
  search(
    first: $first
    after: $after
    sort: $sort
    term: $term
    selectedFacets: $selectedFacets
  ) {
    products {
      pageInfo {
        totalCount
      }
      edges {
        node {
          id: productID
          slug
          sku
          brand {
            brandName: name
            name
          }
          name
          gtin
          isVariantOf {
            productGroupID
            name
          }
          image {
            url
            alternateName
          }
          offers {
            lowPrice
            offers {
              price
              listPrice
              quantity
              seller {
                identifier
              }
            }
          }
        }
      }
    }
  }
}

