ServerCollectionPageQuery:
query ServerCollectionPageQuery($slug: String!) {
  collection(slug: $slug) {
    seo {
      title
      description
    }
    breadcrumbList {
      itemListElement {
        item
        name
        position
      }
    }
    meta {
      selectedFacets {
        key
        value
      }
    }
  }
  allProducts(first: 6, after: "0") {
    edges {
      node {
        id: productID
        slug
        sku
        brand {
          brandName: name
          name
        }
        name
        gtin
        isVariantOf {
          productGroupID
          name
        }
        image {
          url
          alternateName
        }
        offers {
          lowPrice
          offers {
            availability
            price
            listPrice
            quantity
            seller {
              identifier
            }
          }
        }
      }
    }
  }
}

