ValidateCartMutation:
mutation ValidateCartMutation($cart: IStoreCart!) {
  validateCart(cart: $cart) {
    order {
      orderNumber
      acceptedOffer {
        seller {
          identifier
        }
        quantity
        price
        listPrice
        itemOffered {
          sku
          name
          image {
            url
            alternateName
          }
        }
      }
    }
    messages {
      text
      status
    }
  }
}

